import { Config } from '../interfaces/config.js';
import { CreateIntentOptions, CreateIntentResponse, IntentMetadataOptions, IntentMetadataResponse } from '../interfaces/intents.js';
import '../types/index.js';
import '../interfaces/sessions.js';

declare class PaymentIntent {
    private network;
    private options;
    constructor(network: string, options: Config);
    /**
     * Create a new payment intent
     *
     * @returns
     * Returns payment intent object along with `order_id`, `intent_id` and `intent_secret_key`
     * @example
     * ```ts
     * const hipBagItem = {
     *   name: "Throwback Hip Bag",
     *   price: 4,
     *   image: "https://imgur.com/EntGcVQ.png",
     *   quantity: 5
     * }
     *
     * const session = await candypay.paymentIntent.create({
     *   tokens: ["samo", "shdw"],
     *   items: [hipBagItem],
     * })
     * ```
     */
    create(options: CreateIntentOptions): Promise<CreateIntentResponse>;
    /**
     * Fetch metadata of a payment intent
     * @example
     * ```ts
     * const intentID = "cp_pay_34059991-d572-46db-bd1c-225e467ee414"
     *
     * const metadata = await candypay.paymentIntent.metadata({
     *   intent_id: intentID
     * })
     * ```
     */
    metadata(options: IntentMetadataOptions): Promise<IntentMetadataResponse>;
}

export { PaymentIntent };
