import { Config } from '../interfaces/config.js';
import { CreateSessionOptions, CreateSessionResponse, SessionMetadataOptions, SessionMetadataResponse } from '../interfaces/sessions.js';
import '../types/index.js';

declare class Session {
    private network;
    private options;
    constructor(network: string, options: Config);
    /**
     * Create a new checkout session
     *
     * @returns
     * Returns checkout session object along with `order_id` and `payment_url`
     * @see
     * https://docs.candypay.fun/checkout/node-sdk.html#create
     * @example
     * ```ts
     * const hipBagItem = {
     *   name: "Throwback Hip Bag",
     *   price: 4,
     *   image: "https://imgur.com/EntGcVQ.png",
     *   quantity: 5
     * }
     *
     * const session = await candypay.session.create({
     *   success_url: "https://your-website.com/checkout/success",
     *   cancel_url: "https://your-website.com/checkout/cancel",
     *   tokens: ["samo", "shdw"],
     *   items: [hipBagItem],
     *   shipping_fees: 0.43
     * })
     *
     * const paymentURL = session.redirect_url
     * ```
     */
    create(options: CreateSessionOptions): Promise<CreateSessionResponse>;
    /**
     * Fetch metadata of a checkout session
     * @see
     * https://docs.candypay.fun/checkout/node-sdk.html#metadata
     * @example
     * ```ts
     * const sessionID = "cp_pay_34059991-d572-46db-bd1c-225e467ee414"
     *
     * const metadata = await candypay.session.metadata({
     *   session_id: sessionID
     * })
     * ```
     */
    metadata(options: SessionMetadataOptions): Promise<SessionMetadataResponse>;
    /**
     * Generate payment URL for a provided session ID
     * @see
     * https://docs.candypay.fun/checkout/node-sdk.html#generateredirecturl
     * @example
     * ```ts
     * const sessionID = "cp_pay_34059991-d572-46db-bd1c-225e467ee414";
     *
     * const paymentURL = candypay.session.generatePaymentURL({
     *   session_id: sessionID
     * })
     * ```
     */
    generatePaymentURL(options: {
        session_id: string;
    }): string;
}

export { Session };
